package day7;

public class test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		float uses IEEE single precision format
		float a;
		a = 3.1415f;
		System.out.println(a);
//		output 3.1415
		
		float b;
		b = 3.14159265f;
		System.out.println(b);
//		output 3.1415927
		
//		vid example
		float c;
		c = 25.17f;
		System.out.println(c);
//		output 25.17
		
//		double uses IEEE double precision format
		double d;
		d = 3.14159265;
		System.out.println(d);
//		output 3.14159265
		
		long e;
		e = 31415;
		System.out.println(e);
//		output 31415
		


	}

}
